import React, { Component } from 'react'

class Ninja extends Component {
  render() {
      const {name, age} = this
    return (
      <div>
        <h3>my name is {this.props.name}</h3>
        <h4>I am {this.props.age} yearsold.</h4>
      </div>
    )
  }
}
export default Ninja;
