import React, { Component } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';


class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            count: 0,
            name: 'Asabeneh',
            color: 'gray',
            status:'loggedIn'
        }
        this.addOneBtn = this.addOneBtn.bind(this);
        this.changeNameBtn = this.changeNameBtn.bind(this);
        this.changeColorBtn= this.changeColorBtn.bind(this);
    }
    addOneBtn() {
        this.setState({
            count: this.state.count + 1
        })
    }
    changeNameBtn() {
        let name = this.state.name === 'Asabeneh' ? 'David' : 'Asabeneh'
        this.setState({name})
    }
    changeColorBtn(){
        let color= this.state.color == 'gray' ? 'blue' : 'gray'
        this.setState({color})
    }
    login(){
        let status= this.state.status == 'loggedIn'? 'log out' : 'log in'
        this.setState({
            alert
        })
    }

    render() {
        return <div>
            <p>please Log In</p>
            <button onClick={this.login}>log in</button>
            <h1>Count:{this.state.count}</h1>
            <button onClick={this.addOneBtn}>Add one</button>
            <button onClick={this.changeNameBtn}>change name</button>
            <button onClick={this.changeColorBtn}> change color</button>
            <hr />

            <hr />
            <Header
                const content="Let's start with React"
                let firstName="Asabeneh"
                let lastName="Yatayeh"
                const year={new Date().getFullYear()}

            />
            <Main
                const skills={['HTML', 'CSS', 'JAVASCRIPT', 'REACT']}
            />
            <Footer />
        </div >
    }
}
