import React, { Component } from 'react'

class Ninja extends Component {
    render() {
        const { ninjas } = this.props
        const ninjaList = ninjas.map(ninja => {
            return (
                <div key={ninja.id}>
                    <h3>my name is {ninja.name}</h3>
                    <h4>I am {ninja.age} yearsold.</h4>
                </div>
            )
        })
        return (
            <div>
                {ninjaList}
            </div>
        )
    }
}
export default Ninja;
