import React, { Component } from 'react';
import Header from './Header';
import Main from './Main';
import Footer from './Footer';


class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            count: 0,
            name: 'Asabeneh'
        }
        this.addOneBtn = this.addOneBtn.bind(this);
        this.changeNameBtn = this.changeNameBtn.bind(this);
    }
    addOneBtn() {
        this.setState({
            count: this.state.count + 1
        })
    }
    changeNameBtn() {
        let name = this.state.name === 'Asabeneh' ? 'David' : 'Asabeneh'
        this.setState({ name })
    }


    render() {
        return <div>
            <h1>Count:{this.state.count}</h1>
            <button onClick={this.addOneBtn}>Add one</button>
            <button onClick={this.changeNameBtn}>change name</button>
            <hr />

            <hr />
            <Header
                const content="Let's start with React"
                let firstName="Asabeneh"
                let lastName="Yatayeh"
                const year={new Date().getFullYear()}

            />
            <Main
                const skills={['HTML', 'CSS', 'JAVASCRIPT', 'REACT']}
            />
            <Footer />
        </div >
    }
}
